import cv2 as cvfrom matplotlib import pyplot as pltimport numpy as npimport os# Define o Diretório de Trbalhoos.chdir('/Users/valter/Documents/dev/Estudos/Pos-IA-ML/UC7_Desenvolvimento_Aplicacoes_IA/Tema02/Bloco01/1-Filtros')# Carregar as imagensimg = cv.imread('imagem.jpg')img2 = cv.imread('imagem2.png')# Converter de BGR para RGBimg_rgb = cv.cvtColor(img, cv.COLOR_BGR2RGB)img2_rgb = cv.cvtColor(img2, cv.COLOR_BGR2RGB)# Exibir a primeira imagemplt.imshow(img_rgb)# plt.axis('off')  # Opcional: remover os eixosplt.show()# Exibir a segunda imagemplt.imshow(img2_rgb)# plt.axis('off')  # Opcional: remover os eixosplt.show()# **************************# Suavização de imagem para redução de ruído. Perde-se qualidade nesse processo# **************************kernel = np.ones((5,5), np.float32) / 25dst = cv.filter2D(img_rgb, -1, kernel)dst2 = cv.filter2D(img2_rgb, -1, kernel)plt.imshow(dst)plt.show()plt.imshow(dst2)plt.show()# Lado a ladoplt.subplot(121), plt.imshow(img_rgb), plt.title('Original')plt.subplot(122), plt.imshow(dst), plt.title('Media 1x')plt.show()# Lado a ladoplt.subplot(121), plt.imshow(img2_rgb), plt.title('Original')plt.subplot(122), plt.imshow(dst2), plt.title('Media 1x')plt.show()# **************************# limpezaplt.clf()# **************************# Usando atalhos do Python# **************************blur = cv.blur(img_rgb, (5,5))plt.imshow(blur)plt.show()# **************************