import cv2from matplotlib import pyplot as pltimport numpy as np# limpezaplt.clf()img = cv2.imread('assinatura.png')img = cv2.cvtColor(img, cv2.COLOR_BGR2RGB)plt.imshow(img)plt.show()struct = np.ones((5,5), np.uint8)img_erosao = cv2.erode(img, struct, iterations=3)plt.imshow(img_erosao)plt.show()img_dilatacao = cv2.dilate(img, struct, iterations=1)plt.imshow(img_dilatacao)plt.show()img_erosao2 = cv2.erode(img_dilatacao, struct, iterations=3)plt.imshow(img_erosao2)plt.show()