import cv2import matplotlib.pyplot as pltimport numpy as npfrom os import listdir# LIMPEZAplt.clf()def extract_data(arquivo):    imgRet = cv2.imread(arquivo, 0)    imgRet = cv2.cvtColor(imgRet, cv2.COLOR_RGB2BGR)    imgRet = imgRet.ravel()    imgMedia = np.mean(imgRet)    return imgMedialista_arquivos = listdir('imgs')lista_arquivos.sort()lista_medias = list()for nome_arquivo in lista_arquivos:    media = extract_data('imgs/' + nome_arquivo)    print(media)    lista_medias.append(media)plt.plot(lista_medias)plt.show()